document.addEventListener("DOMContentLoaded",()=>{document.querySelectorAll("[open-modal]").forEach(e=>{e.addEventListener("click",()=>{let t=document.getElementById(e.dataset.modal);window.onclick=(e=>{e.target==t&&(t.style.display="none")}),"block"===window.getComputedStyle(t).getPropertyValue("display")?t.style.display="none":t.style.display="block"})}),document.querySelectorAll(".modal-close-btn").forEach(e=>{e.addEventListener("click",()=>{document.getElementById(e.dataset.modal).style.display="none"})})});