document.addEventListener("DOMContentLoaded",()=>{document.querySelectorAll(".topnav-hamburger-menu").forEach(e=>{document.getElementById(e.dataset.target).classList.add("topnav-list"),e.addEventListener("click",()=>{"block"===window.getComputedStyle(document.getElementById(e.dataset.target)).getPropertyValue("display")?(e.innerHTML="&#x2630;",document.getElementById(e.dataset.target).style.display="none"):(e.innerHTML="&times;",document.getElementById(e.dataset.target).style.display="block")})}),document.querySelectorAll(".sidenav-menu-btn").forEach(e=>{e.addEventListener("click",()=>{document.querySelectorAll(".sidenav-close-btn").forEach(e=>{e.addEventListener("click",()=>{document.getElementById(e.dataset.target).style.width="0%"})}),window.innerWidth<=768?document.getElementById(e.dataset.target).style.width="100%":document.getElementById(e.dataset.target).style.width="250px"})}),document.querySelectorAll(".fixed-sidenav-dropdown-btn").forEach(e=>{e.addEventListener("click",()=>{"block"===window.getComputedStyle(document.getElementById(e.dataset.dropdown)).getPropertyValue("display")?document.getElementById(e.dataset.dropdown).style.display="none":document.getElementById(e.dataset.dropdown).style.display="block"})})});