document.addEventListener("DOMContentLoaded",()=>{document.querySelectorAll(".tab").forEach(t=>{t.childNodes.forEach(e=>{e.classList&&e.classList.contains("tab-btn")&&e.addEventListener("click",()=>{document.querySelectorAll(".tab-content").forEach(e=>{e.parentElement.id===t.dataset.tab&&(e.style.display="none")}),document.getElementById(e.dataset.content).style.display="block"})})})});